import maya.mel as mel
import maya.cmds as cmds

class rewindStopHold:
    def __init__(self, oldStartTime=0):
        self.oldStartTime = oldStartTime
    def returnStartTime(self):
        return self.oldStartTime

oldStartTime = cmds.currentTime( query=True )
rewindStopHoldVar = rewindStopHold(oldStartTime)
mel.eval('togglePlayback; evalEcho("play -forward false")')

if (`popupMenu -exists tempMM`) { deleteUI tempMM; }
popupMenu -button 1 -ctl false -alt false -sh false -allowOptionBoxes true -parent `findPanelPopupParent` -mm 1 tempMM; 
